#pragma once

struct MpptData
{
   MpptData();

   enum Type
   {
      Helianthus = 0,
      Dilithium = 1
   };

   enum Mode
   {
      Static = 0,
      Dynamic = 1,
      IvTrace = 2,
      Open = 3,
      Invalid = 4
   };

   Type type;
   Mode mode;

   float voltageIn;
   float voltageOut;
   float currentIn;
   float currentOut;
};
